mapper.insert:
INSERT INTO QNA(
title, q_content,
reg_date, edit_date,
category,writer_id
)VALUES(
#{title},#{qContent},now(),now(),#{category},#{writerId}
)

mapper.select:
SELECT id,title,category,DATE_FORMAT(reg_date, '%Y-%m-%d')
as reg_date
FROM QNA 
WHERE writer_id=#{writerId}
ORDER BY id DESC
LIMIT #{limitStart}, #{listCount};
