mapper.select:
SELECT DATE_FORMAT(i.reg_data , '%Y-%m-%d') as reg_data , COUNT(i.reg_data) as reg, DATE_FORMAT(m.join_date , '%Y-%m-%d') as join_date , COUNT(m.join_date) as jon FROM item_order i left outer JOIN member m ON i.reg_data = m.join_date GROUP BY DATE_FORMAT(i.reg_data, '%Y-%m-%d'),DATE_FORMAT(m.join_date,'%Y-%m-%d')
union
SELECT DATE_FORMAT(i.reg_data , '%Y-%m-%d') as reg_data , COUNT(i.reg_data) as reg, DATE_FORMAT(m.join_date , '%Y-%m-%d') as join_date , COUNT(m.join_date) as jon FROM item_order i right outer JOIN member m ON i.reg_data = m.join_date GROUP BY DATE_FORMAT(i.reg_data, '%Y-%m-%d'),DATE_FORMAT(m.join_date,'%Y-%m-%d');
